import matplotlib.pyplot as plt
	
# read file in lib
def eachFile(filepath):
	pathDir = os.listdir(filepath)
	child = []
	for allDir in pathDir:
		child.append(os.path.join('%s/%s' % (filepath, allDir)))
	return child

# read lines in filename
def read2Lines(filename):
	file = open(filename, 'r')
	lines=[]
	for x in file.readlines():
		if x.strip() != '':
			lines.append(x.strip())
	file.close()
	return lines

# split words
def sent2word(sentence):
	segList = jieba.cut(sentence)
	segResult = []
	for w in segList:
		segResult.append(w)
	stopwords = read2Lines('stop_words.txt')
	newSent = []
	for word in segResult:
		if word + '\n' in stopwords:
			continue
		else:
			newSent.append(word)
	return newSent

# split words without stopwords
def returnsegResult(sentence):
	segResult = []
	segList = jieba.cut(sentence)
	for w in segList:
		segResult.append(w)
	return segResult

def listToDict(wordlist):
	word_dict = {}
	for x in range(0, len(wordlist)):
		word_dict[wordlist[x]] = x
	return word_dict
	
# sentiment measure
def classifyWords(wordDict):
	# sentiment words
	senList = read2Lines('BosonNLP_sentiment_score.txt')
	senDict = defaultdict()
	for s in senList:
		senDict[s.split(' ')[0]] = s.split(' ')[1]
	# not words
	notList = read2Lines('notDict.txt')
	# degree adv
	degreeList = read2Lines('degreeDict.txt')
	degreeDict = defaultdict()
	for d in degreeList:
		degreeDict[d.split(' ')[0]] = d.split(' ')[1]
	senWord = defaultdict()
	notWord = defaultdict()
	degreeWord = defaultdict()
	for word in wordDict.keys():
		if word in senDict.keys() and word not in notList and word not in degreeDict.keys():
			senWord[wordDict[word]] = senDict[word]
		elif word in notList and word not in degreeDict.keys():
			notWord[wordDict[word]] = -1
		elif word in degreeDict.keys():
			degreeWord[wordDict[word]] = degreeDict[word]
	return senWord, notWord, degreeWord

# sentiment score
def scoreSent(senWord, notWord, degreeWord, segResult):
	W = 1
	score = 0
	senLoc = senWord.keys()
	notLoc = notWord.keys()
	degreeLoc = degreeWord.keys()
	senloc = -1
	#notloc = -1
	#degreeloc = -1
	for i in range(0, len(segResult)):
		if i in senLoc:
			senloc += 1
			score += W * float(senWord[i])
			# print "score = %f" % score
			if senloc < len(senLoc) - 1:
				for j in range(senLoc[senloc], senLoc[senloc + 1]):
					if j in notLoc:
						W *= -1
					elif j in degreeLoc:
						W *= float(degreeWord[j])
		if senloc < len(senLoc) - 1:
			i = senLoc[senloc + 1]
	return score

def plt_img():
	plt.figure()
	plt.title('Sentiment Score')
	plt.xlabel('x')
	plt.ylabel('y')  
	plt.axis([0,3000,-10,10])
	plt.grid(True)
	return plt
	
